import React from 'react';
import { Card } from 'antd';
import { Draggable } from 'react-beautiful-dnd';

import { StageEmployee } from 'components';

import './style.scss';

interface IProps {
  stage: any;
  people: any;
  innerRef: any;
}

export default class StageContainer extends React.Component<IProps> {
  render() {
    return (
      <Card
        size='small'
        className='stage-container'
        title={
          <div className='stage-container__head'>
            <span className='stage-container__title'>
              {this.props.stage.title}
            </span>
            <span className='stage-container__count'>
              {this.props.people.length}
            </span>
          </div>
        }
        bodyStyle={{
          height: 'calc(100% - 57px)',
          overflow: 'auto',
          padding: 12,
        }}>
        <div ref={this.props.innerRef}>
          {this.props.people.map((person: any, index: number) => (
            <Draggable key={person.id} draggableId={person.id} index={index}>
              {provided => (
                <StageEmployee
                  person={person}
                  index={index}
                  provided={provided}
                />
              )}
            </Draggable>
          ))}
        </div>
      </Card>
    );
  }
}
