import React from 'react';
import { Icon, Menu, Dropdown, Button, Card, Modal } from 'antd';

import './style.scss';

const { Item } = Menu;
const { confirm } = Modal;

function handleClick() {
  console.log('Title clicked');
}

function showDeleteConfirm(handleDelete: () => {}) {
  confirm({
    title: 'آیا از آرشیو این موقعیت شغلی اطمینان دارید؟',
    okText: 'بلی',
    okType: 'danger',
    cancelText: 'خیر',
    maskClosable: true,
    onOk() {
      handleDelete();
    },
    onCancel() {},
  });
}

interface IProps {
  title: string;
  numOfCandidates: number;
  handleEdit: () => void;
  handleDelete: () => {};
}

export const JobCard: React.FC<IProps> = props => {
  function handleMenuClick(e: any) {
    switch (e.key) {
      case '1':
        props.handleEdit();
        break;
      case '2':
        showDeleteConfirm(props.handleDelete);
        break;
      default:
        break;
    }
  }

  const menu = (
    <Menu onClick={handleMenuClick}>
      <Item key='1'>ویرایش</Item>
      <Item key='2'>آرشیو</Item>
    </Menu>
  );

  return (
    <Card
      className='job-card'
      size='small'
      title={
        <span className='job-card__title' onClick={handleClick}>
          {props.title}
        </span>
      }
      extra={
        <div>
          <Button className='job-card__button'>مشاهده کاندیدها</Button>
          <Dropdown overlay={menu}>
            <a href='# '>
              <Icon type='caret-down' className='job-card__icon' />
            </a>
          </Dropdown>
        </div>
      }>
      <p className='job-card__body'>{props.numOfCandidates} کاندید</p>
    </Card>
  );
};
