import React, { useState } from 'react';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import { Layout, Dropdown, Menu, Icon } from 'antd';

import { StageContainer } from 'containers';
import preload from './preload';
import 'layouts/EmployerLayout/style.scss';
import './style.scss';

const { Header, Content } = Layout;

function handleMenuClick(e: any) {
  console.log('click', e);
}

const menu = (
  <Menu onClick={handleMenuClick}>
    <Menu.Item key='1'>آرشیو</Menu.Item>
  </Menu>
);

export const EmployerStagePage: React.FC = () => {
  const [data, setData] = useState(preload);

  const onDragEnd = (result: any) => {
    const { source, destination, draggableId } = result;
    if (!destination) {
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const start = (data.stages as any)[source.droppableId];
    const end = (data.stages as any)[destination.droppableId];

    if (start === end) {
      const newPersonIds = Array.from(start.personIds);
      newPersonIds.splice(source.index, 1);
      newPersonIds.splice(destination.index, 0, draggableId);

      const newStage = {
        ...start,
        personIds: newPersonIds,
      };

      const newState = {
        ...data,
        stages: {
          ...data.stages,
          [newStage.id]: newStage,
        },
      };

      setData(newState);
      return;
    }

    const startPersonIds = Array.from(start.personIds);
    startPersonIds.splice(source.index, 1);
    const newStart = {
      ...start,
      personIds: startPersonIds,
    };

    const endPersonIds = Array.from(end.personIds);
    endPersonIds.splice(destination.index, 0, draggableId);
    const newEnd = {
      ...end,
      personIds: endPersonIds,
    };

    const newState = {
      ...data,
      stages: {
        ...data.stages,
        [newStart.id]: newStart,
        [newEnd.id]: newEnd,
      },
    };

    setData(newState);
  };

  return (
    <>
      <Header className='employer-layout__header employer-stage-page__header'>
        <p className='employer-stage-page__title'>
          <span>موقعیت شغلی</span>&nbsp;
          <span className='employer-stage-page__title--sub'>
            طراح تجربه کاربری
          </span>
        </p>
        <Dropdown.Button
          className='employer-stage-page__header__dropdown'
          overlay={menu}
          icon={<Icon type='down' />}>
          <div>
            <Icon type='edit' />
            &nbsp;ویرایش
          </div>
        </Dropdown.Button>
      </Header>
      <DragDropContext onDragEnd={onDragEnd}>
        <Content className='employer-layout__content'>
          <div className='employer-stage-page__content'>
            {data.stageOrder.map((stageId: any) => {
              const stage = (data.stages as any)[stageId];
              const people = stage.personIds.map(
                (personId: any) => (data.people as any)[personId],
              );

              return (
                <Droppable key={stage.id} droppableId={stage.id}>
                  {(provided, snapshot) => (
                    <StageContainer
                      stage={stage}
                      people={people}
                      provided={provided}
                      isDraggingOver={snapshot.isDraggingOver}
                      innerRef={provided.innerRef}
                      {...provided.droppableProps}>
                      {provided.placeholder}
                    </StageContainer>
                  )}
                </Droppable>
              );
            })}
          </div>
        </Content>
      </DragDropContext>
    </>
  );
};
