import Api from 'Api/api';
import { call, put, take } from 'redux-saga/effects';
import {
  REGISTER_REQUEST,
  REGISTER_SUCCESS,
  REGISTER_ERROR,
} from 'store/actionTypes/registerActionType';
import {
  registerSuccess,
  registerError,
  saveRegisterToken,
} from 'store/actions/registerActions';

export function fakeAutoRise(
  email: string,
  password: string,
  fullName: string,
  companyName: string,
) {
  return new Promise(async (resolve, reject) => {
    let headerConfig = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      const result = await Api.post(
        'users/reg',
        {
          email: email,
          password: password,
          fullname: fullName,
          companyName: companyName,
        },
        headerConfig,
      );
      // console.log(result.data.accessToken);
      window.localStorage.setItem('accessToken', result.data.accessToken);
      window.localStorage.setItem('refreshToken', result.data.refreshToken);
      resolve(result.data.accessToken);
    } catch (e) {
      reject(e);
    }
  });
}

export function deleteToken() {
  return new Promise(async (resolve, reject) => {
    try {
      window.localStorage.setItem('accessToken', '');
      resolve('');
    } catch (e) {
      reject(e);
    }
  });
}

export function* autorize(
  email: string,
  password: string,
  fullName: string,
  companyName: string,
) {
  try {
    const token = yield call(
      fakeAutoRise,
      email,
      password,
      fullName,
      companyName,
    );
    yield put(registerSuccess(REGISTER_SUCCESS));
    yield put(saveRegisterToken(token));
  } catch (error) {
    yield put(registerError(REGISTER_ERROR));
  }
}

export function* registerFlow() {
  while (true) {
    const { email, password, fullname, companyName } = yield take(
      REGISTER_REQUEST,
    );

    yield call(autorize, email, password, fullname, companyName);
  }
}
